mapper.update:
update userdata
set NAME = #{NAME}, ID = #{ID}, PASSWORD = #{PASSWORD}, PHONE_NUMBER = #{PHONE_NUMBER}, BIRTHDAY = #{BIRTHDAY}, EMAIL = #{EMAIL}, SMS_AD = #{SMS_AD}, EMAIL_AD = #{EMAIL_AD}
where USER_UID = #{USER_UID}

mapper.insert:
insert into userdata (USER_UID, NAME, ID, PASSWORD, PHONE_NUMBER, BIRTHDAY, EMAIL, SMS_AD, EMAIL_AD) VALUE (#{USER_UID},#{NAME},#{ID},#{PASSWORD},#{PHONE_NUMBER},#{BIRTHDAY},#{EMAIL}
, '1'
, '0'
, '1'
, '0'
)
